mapper.select:
SELECT ADMIN_ID
FROM TBL_ADMIN
WHERE ADMIN_LOGIN_ID = #{adminLoginId} AND ADMIN_PASSWORD = #{adminPassword}

SELECT COUNT(ADMIN_ID)
FROM TBL_ADMIN
WHERE ADMIN_LOGIN_ID = #{adminLoginId} AND ADMIN_PASSWORD = #{adminPassword}

SELECT ADMIN_LOGIN_ID
FROM TBL_ADMIN
WHERE ADMIN_ID = #{adminId}

SELECT STATUS
FROM TBL_ADMIN
WHERE ADMIN_LOGIN_ID = #{adminLoginId}
