mapper.select:
SELECT TITLE, DEBATE_ID, MEETING_ID, GROUP_START_DATE, GROUP_END_DATE,BOOK_NAME, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_BOOK_ID, DEBATE_TYPE_ID, OUTLINE, DEBATE_TYPE, MEETING_TYPE FROM ( SELECT ROWNUM AS RNUM, DEBATE_ID, TITLE, MEETING_ID, BOOK_NAME, GROUP_START_DATE, GROUP_END_DATE, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_BOOK_ID, DEBATE_TYPE_ID, OUTLINE, DEBATE_TYPE, MEETING_TYPE FROM( SELECT D.DEBATE_ID ,D.TITLE, D.MEETING_ID,TB.TITLE AS BOOK_NAME, TO_CHAR(D.GROUP_START_DATE, 'YYYY-MM-DD') AS GROUP_START_DATE, TO_CHAR(D.GROUP_END_DATE, 'YYYY-MM-DD') AS GROUP_END_DATE, TO_CHAR(D.DEBATE_START_DATE, 'YYYY-MM-DD') AS DEBATE_START_DATE, TO_CHAR(D.DEBATE_END_DATE, 'YYYY-MM-DD') AS DEBATE_END_DATE, B.DEBATE_BOOK_ID, D.DEBATE_TYPE_ID, D.OUTLINE, DT.DEBATE_TYPE, MT.MEETING_TYPE FROM TBL_DEBATE D JOIN TBL_DEBATE_BOOK B ON D.DEBATE_ID = B.DEBATE_ID JOIN TBL_DEBATE_TYPE DT ON D.DEBATE_TYPE_ID = DT.DEBATE_TYPE_ID JOIN TBL_MEETING_TYPE MT ON D.MEETING_ID = MT.MEETING_ID JOIN TBL_BOOK TB ON B.ISBN13 = TB.ISBN13
WHERE TO_CHAR(DEBATE_START_DATE,'YYYY-MM-DD') <= TO_CHAR(SYSDATE, 'YYYY-MM-DD') AND TO_CHAR(DEBATE_END_DATE,'YYYY-MM-DD') > TO_CHAR(SYSDATE, 'YYYY-MM-DD')
ORDER BY DEBATE_END_DATE DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1) * #{amount}

SELECT TITLE, DEBATE_ID, MEETING_ID, GROUP_START_DATE, GROUP_END_DATE, BOOK_NAME, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_BOOK_ID, DEBATE_TYPE_ID, OUTLINE,DEBATE_TYPE, MEETING_TYPE FROM ( SELECT ROWNUM AS RNUM, DEBATE_ID, TITLE, MEETING_ID, BOOK_NAME, GROUP_START_DATE, GROUP_END_DATE, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_BOOK_ID, DEBATE_TYPE_ID, OUTLINE, DEBATE_TYPE, MEETING_TYPE FROM( SELECT DEBATE_ID, TITLE, MEETING_ID, BOOK_NAME, GROUP_START_DATE, GROUP_END_DATE, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_BOOK_ID, DEBATE_TYPE_ID, OUTLINE, DEBATE_TYPE, MEETING_TYPE FROM ( SELECT D.DEBATE_ID, D.TITLE, D.MEETING_ID, TB.TITLE AS BOOK_NAME, TO_CHAR(D.GROUP_START_DATE, 'YYYY-MM-DD') AS GROUP_START_DATE, TO_CHAR(D.GROUP_END_DATE, 'YYYY-MM-DD') AS GROUP_END_DATE, TO_CHAR(D.DEBATE_START_DATE, 'YYYY-MM-DD') AS DEBATE_START_DATE, TO_CHAR(D.DEBATE_END_DATE, 'YYYY-MM-DD') AS DEBATE_END_DATE, B.DEBATE_BOOK_ID, D.DEBATE_TYPE_ID, D.OUTLINE, DT.DEBATE_TYPE, MT.MEETING_TYPE FROM TBL_DEBATE D JOIN TBL_DEBATE_BOOK B ON D.DEBATE_ID = B.DEBATE_ID JOIN TBL_DEBATE_TYPE DT ON D.DEBATE_TYPE_ID = DT.DEBATE_TYPE_ID JOIN TBL_MEETING_TYPE MT ON D.MEETING_ID = MT.MEETING_ID JOIN TBL_BOOK TB ON B.ISBN13 = TB.ISBN13
WHERE TO_CHAR(DEBATE_START_DATE,'YYYY-MM-DD') <= TO_CHAR(SYSDATE, 'YYYY-MM-DD') AND TO_CHAR(DEBATE_END_DATE,'YYYY-MM-DD') >TO_CHAR(SYSDATE, 'YYYY-MM-DD')
ORDER BY DEBATE_END_DATE DESC )
BOOK_NAME LIKE '%' || #{keyword} || '%'
MEETING_TYPE LIKE '%' || #{keyword} || '%'
)
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1) * #{amount}

SELECT TITLE, DEBATE_ID, MEETING_ID, BOOK_NAME, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_TYPE_ID, MEETING_TYPE,DEBATE_TYPE FROM ( SELECT ROWNUM AS RNUM, DEBATE_ID, TITLE, MEETING_ID, BOOK_NAME, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_TYPE_ID, MEETING_TYPE,DEBATE_TYPE FROM( SELECT D.DEBATE_ID, D.TITLE, D.MEETING_ID, TB.TITLE AS BOOK_NAME, TO_CHAR(D.DEBATE_START_DATE, 'YYYY-MM-DD') AS DEBATE_START_DATE, TO_CHAR(D.DEBATE_END_DATE, 'YYYY-MM-DD') AS DEBATE_END_DATE, D.DEBATE_TYPE_ID, DT.DEBATE_TYPE, MT.MEETING_TYPE FROM TBL_DEBATE D JOIN TBL_DEBATE_BOOK B ON D.DEBATE_ID = B.DEBATE_ID JOIN TBL_DEBATE_TYPE DT ON D.DEBATE_TYPE_ID = DT.DEBATE_TYPE_ID JOIN TBL_MEETING_TYPE MT ON D.MEETING_ID = MT.MEETING_ID JOIN TBL_BOOK TB ON B.ISBN13 = TB.ISBN13
WHERE TO_CHAR(DEBATE_END_DATE,'YYYY-MM-DD') < TO_CHAR(SYSDATE, 'YYYY-MM-DD')
ORDER BY D.DEBATE_END_DATE DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1) * #{amount}

